create database quyen_gop_tu_thien;
go
use quyen_gop_tu_thien; 
go
create table nguoi_dung (ma_nguoi_dung int identity(1,1) primary key, ten nvarchar(50), email varchar(100), so_dien_thoai varchar(10), password varchar(50), vai_tro varchar(5), trang_thai nvarchar(20));
create table phan_loai_su_kien (loai_su_kien nvarchar(50) primary key, ky_hieu varchar(5));
create table to_chuc_trien_khai_su_kien (ten_to_chuc nvarchar(50) primary key, thong_tin_to_chuc nvarchar(2000), logo nvarchar(150));
create table su_kien (ma_su_kien varchar(50) primary key, ngay_them date, mo_ta_su_kien nvarchar(200), chi_tiet_su_kien nvarchar(max), so_tien_can_hoan_thanh money, trang_thai nvarchar(20), ten_to_chuc nvarchar(50), foreign key(ten_to_chuc) references to_chuc_trien_khai_su_kien(ten_to_chuc) on update cascade);
create table hinh_anh (so_thu_tu smallint, link_anh nvarchar(100), noi_dung nvarchar(500), ma_su_kien varchar(50), foreign key(ma_su_kien) references su_kien(ma_su_kien) on delete cascade on update cascade);
create table quyen_gop (ma_quyen_gop int identity(1,1) primary key, ma_nguoi_dung int, foreign key(ma_nguoi_dung) references nguoi_dung(ma_nguoi_dung) on delete cascade on update cascade, ma_su_kien varchar(50), foreign key (ma_su_kien) references su_kien (ma_su_kien) on delete cascade on update cascade, so_tien money, ngay_quyen_gop date);
go
